:root {
  --primary: #7b809a;
  --primary-light: #8f93a9;
  --primary-dark: #666d86;
  --primary-hover: #666d86;
  --secondary: #344767;
  --secondary-light: #384d70;
  --secondary-dark: #2c3c56;
  --accent: #e91e63;
  --error: #f44335;
  --warning: #fb8c00;
  --success: #4caf50;
  --info: #1A73E8;
  --background: #f0f2f5;
  --surface: #ffffff;
  --surface-hover: #f8f9fa;
  --text-primary: rgba(52, 71, 103, 0.87);
  --text-secondary: rgba(52, 71, 103, 0.6);
  --text-disabled: rgba(52, 71, 103, 0.38);
  --text-inverse: #ffffff;
  --border-color: #d2d6da;
  --border-light: #e9ecef;
  --border-dark: #344767;
  --border-focus: #e91e63;
}

/* Typography */
body {
  font-family: Roboto, Helvetica, Arial, sans-serif;
  font-size: 1rem;
  line-height: 1.5;
  color: var(--text-primary);
  background-color: var(--background);
}

/* Grid Layout */
.container {
  width: 100%;
  max-width: 1320px;
  margin: 0 auto;
  padding: 0 1rem;
}

.grid {
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  gap: 1.5rem;
}

@media (max-width: 768px) {
  .grid {
    grid-template-columns: 1fr;
  }
}

/* Form Elements */
.form-control {
  width: 100%;
  padding: 0.75rem 1rem;
  font-size: 0.875rem;
  line-height: 1.5;
  color: var(--text-primary);
  background-color: var(--surface);
  border: 1px solid var(--border-color);
  border-radius: 0.375rem;
  transition: all 0.2s ease-in-out;
}

.form-control:focus {
  border-color: var(--border-focus);
  outline: none;
  box-shadow: 0 0 0 0.2rem rgba(233, 30, 99, 0.25);
}

.form-label {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--text-primary);
  margin-bottom: 0.5rem;
}

/* Button Styles */
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 0.625rem 1.5rem;
  font-size: 0.875rem;
  font-weight: 500;
  line-height: 1.5;
  border-radius: 0.375rem;
  transition: all 0.15s ease-in-out;
  cursor: pointer;
  border: none;
}

.btn-primary {
  background-color: var(--primary);
  color: var(--text-inverse);
}

.btn-primary:hover {
  background-color: var(--primary-hover);
}

.btn-secondary {
  background-color: var(--secondary);
  color: var(--text-inverse);
}

.btn-outline {
  border: 1px solid var(--primary);
  color: var(--primary);
  background-color: transparent;
}

/* Table Styles */
.table {
  width: 100%;
  margin-bottom: 1rem;
  background-color: var(--surface);
  border-radius: 0.375rem;
  box-shadow: var(--shadow-sm);
}

.table th,
.table td {
  padding: 1rem;
  border-bottom: 1px solid var(--border-light);
}

.table thead th {
  font-weight: 600;
  color: var(--text-primary);
  background-color: var(--surface);
  border-bottom: 2px solid var(--border-dark);
}

.table tbody tr:hover {
  background-color: var(--surface-hover);
}

/* Card Styles */
.card {
  background-color: var(--surface);
  border-radius: 0.75rem;
  box-shadow: 0 4px 6px -1px rgba(0,0,0,0.1), 0 2px 4px -1px rgba(0,0,0,0.06);
  padding: 1.5rem;
  margin-bottom: 1.5rem;
}

.card-header {
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 1rem;
}

/* Validation States */
.is-invalid {
  border-color: var(--error) !important;
}

.is-valid {
  border-color: var(--success) !important;
}

.error-message {
  color: var(--error);
  font-size: 0.875rem;
  margin-top: 0.25rem;
}

.success-message {
  color: var(--success);
  font-size: 0.875rem;
  margin-top: 0.25rem;
}

/* Focus Visible */
:focus-visible {
  outline: 2px solid var(--border-focus);
  outline-offset: 2px;
}

/* Accessibility */
.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  border: 0;
}

/* RTL Support */
[dir="rtl"] {
  text-align: right;
}

/* Responsive Utilities */
@media (max-width: 576px) {
  .container {
    padding: 0 0.5rem;
  }

  .card {
    padding: 1rem;
  }

  .form-control,
  .btn {
    font-size: 1rem;
    padding: 0.5rem 1rem;
  }
}

/* Print Styles */
@media print {
  .no-print {
    display: none;
  }

  body {
    background: none;
  }

  .card {
    box-shadow: none;
    border: 1px solid var(--border-color);
  }
}

