:root {
  --primary: #0066cc;
  --primary-light: #3385d6;
  --primary-dark: #004499;
  --primary-hover: #0052a3;
  --secondary: #6c757d;
  --secondary-light: #868e96;
  --secondary-dark: #545b62;
  --accent: #17a2b8;
  --error: #dc3545;
  --warning: #ffc107;
  --success: #28a745;
  --info: #17a2b8;
  --background: #ffffff;
  --background-secondary: #f8f9fa;
  --surface: #ffffff;
  --surface-hover: #f8f9fa;
  --text-primary: rgba(0, 0, 0, 0.87);
  --text-secondary: rgba(0, 0, 0, 0.60);
  --text-disabled: rgba(0, 0, 0, 0.38);
  --text-inverse: rgba(255, 255, 255, 0.95);
  --border-default: #dee2e6;
  --border-light: #e9ecef;
  --border-dark: #adb5bd;
  --border-focus: #0066cc;
}

.dashboard-body {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  font-size: 16px;
  line-height: 1.5;
  color: var(--text-primary);
  background-color: var(--background-secondary);
  margin: 0;
  padding: 0;
}

.dashboard-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 24px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 16px;
}

.dashboard-input {
  width: 100%;
  padding: 8px 12px;
  border-radius: 4px;
  border: 1px solid var(--border-default);
  font-size: 16px;
  background-color: var(--surface);
  color: var(--text-primary);
  transition: all 250ms ease;
}

.dashboard-input:focus {
  border-color: var(--border-focus);
  box-shadow: 0 0 0 3px rgba(0, 102, 204, 0.1);
  outline: none;
}

.dashboard-input:disabled {
  background-color: var(--background-secondary);
  color: var(--text-disabled);
  cursor: not-allowed;
}

.dashboard-btn {
  padding: 8px 16px;
  border-radius: 4px;
  font-size: 16px;
  font-weight: 500;
  border: none;
  cursor: pointer;
  transition: all 250ms ease;
}

.dashboard-btn-primary {
  background-color: var(--primary);
  color: var(--text-inverse);
}

.dashboard-btn-primary:hover {
  background-color: var(--primary-hover);
  transform: translateY(-1px);
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.dashboard-btn-secondary {
  background-color: var(--secondary);
  color: var(--text-inverse);
}

.dashboard-btn-secondary:hover {
  background-color: var(--secondary-dark);
  transform: translateY(-1px);
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.dashboard-table {
  width: 100%;
  border-collapse: collapse;
  background-color: var(--surface);
  border-radius: 8px;
  overflow: hidden;
}

.dashboard-table th,
.dashboard-table td {
  padding: 12px;
  border-bottom: 1px solid var(--border-light);
  text-align: left;
}

.dashboard-table th {
  background-color: var(--background-secondary);
  font-weight: 600;
}

.dashboard-form-group {
  margin-bottom: 16px;
}

.dashboard-header-block {
  background-color: var(--surface);
  padding: 24px;
  border-radius: 8px;
  margin-bottom: 24px;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.05);
}

.dashboard-data-block {
  background-color: var(--surface);
  padding: 24px;
  border-radius: 8px;
  margin-bottom: 24px;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.05);
}

.dashboard-checkbox {
  width: 20px;
  height: 20px;
  margin-right: 8px;
}

.dashboard-footer {
  background-color: var(--surface);
  padding: 24px;
  margin-top: 24px;
  border-top: 1px solid var(--border-light);
}

/* Dark Theme */
[data-theme="dark"] {
  --background: #1a1a1a;
  --background-secondary: #2d2d2d;
  --surface: #333333;
  --surface-hover: #404040;
  --text-primary: rgba(255, 255, 255, 0.87);
  --text-secondary: rgba(255, 255, 255, 0.60);
  --text-disabled: rgba(255, 255, 255, 0.38);
  --border-default: #404040;
  --border-light: #4d4d4d;
  --border-dark: #666666;
}

/* RTL Support */
[dir="rtl"] {
  direction: rtl;
  text-align: right;
}

[dir="rtl"] .dashboard-table th,
[dir="rtl"] .dashboard-table td {
  text-align: right;
}

[dir="rtl"] .dashboard-checkbox {
  margin-right: 0;
  margin-left: 8px;
}

/* Form Validation States */
.dashboard-input.is-invalid {
  border-color: var(--error);
}

.dashboard-input.is-valid {
  border-color: var(--success);
}

.dashboard-error-message {
  color: var(--error);
  font-size: 14px;
  margin-top: 4px;
}

/* Responsive Design */
@media (max-width: 768px) {
  .dashboard-container {
    grid-template-columns: 1fr;
  }

  .dashboard-table {
    display: block;
    overflow-x: auto;
  }
}

@media (prefers-reduced-motion: reduce) {
  * {
    transition: none !important;
  }
}

