:root {
  --color-primary: #7b809a;
  --color-primary-light: #8f93a9;
  --color-primary-dark: #666d86;
  --color-primary-hover: #666d86;
  --color-secondary: #344767;
  --color-secondary-light: #384d70;
  --color-secondary-dark: #2c3c56;
  --color-accent: #e91e63;
  --color-error: #f44335;
  --color-warning: #fb8c00;
  --color-success: #4caf50;
  --color-info: #1A73E8;
  --color-background: #f0f2f5;
  --color-background-secondary: #ffffff;
  --color-surface: #ffffff;
  --color-surface-hover: #f8f9fa;
  --color-text-primary: rgba(52, 71, 103, 0.87);
  --color-text-secondary: rgba(52, 71, 103, 0.6);
  --color-text-disabled: rgba(52, 71, 103, 0.38);
  --color-text-inverse: #ffffff;
  --color-link: #1A73E8;
  --color-link-hover: #0B4F94;
  --color-border: #d2d6da;
  --color-border-light: #e9ecef;
  --color-border-dark: #344767;
  --color-border-focus: #e91e63;

  --font-family-primary: Roboto, Helvetica, Arial, sans-serif;
  --font-family-secondary: "Material Icons Round";
  --font-family-mono: SFMono-Regular, Menlo, Monaco, Consolas, monospace;

  --spacing-xs: 0.25rem;
  --spacing-sm: 0.5rem;
  --spacing-md: 1rem;
  --spacing-lg: 1.5rem;
  --spacing-xl: 2rem;
  --spacing-2xl: 3rem;
  --spacing-3xl: 4rem;
  --spacing-4xl: 6rem;
}

body {
  font-family: var(--font-family-primary);
  color: var(--color-text-primary);
  background-color: var(--color-background);
  line-height: 1.5;
  margin: 0;
  padding: 0;
}

.container {
  width: 100%;
  max-width: 1320px;
  margin: 0 auto;
  padding: 0 var(--spacing-md);
}

.grid {
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  gap: 1.5rem;
}

.form-group {
  position: relative;
  margin-bottom: var(--spacing-md);
}

.form-control {
  width: 100%;
  padding: var(--spacing-sm) var(--spacing-md);
  font-size: 0.875rem;
  line-height: 1.5;
  color: var(--color-text-primary);
  background-color: var(--color-surface);
  border: 1px solid var(--color-border);
  border-radius: 0.375rem;
  transition: all 0.2s ease-in-out;
}

.form-control:hover {
  border-color: var(--color-border-dark);
}

.form-control:focus {
  outline: none;
  border-color: var(--color-border-focus);
  box-shadow: 0 0 0 0.2rem rgba(233, 30, 99, 0.25);
}

.form-label {
  position: absolute;
  top: -0.5rem;
  left: 0.75rem;
  padding: 0 0.25rem;
  background-color: var(--color-surface);
  font-size: 0.75rem;
  color: var(--color-text-secondary);
  transition: all 0.2s ease-in-out;
}

.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 0.625rem 1.5rem;
  font-size: 0.875rem;
  font-weight: 700;
  line-height: 1.5;
  text-align: center;
  text-decoration: none;
  border-radius: 0.375rem;
  transition: all 0.2s ease-in-out;
  cursor: pointer;
}

.btn-primary {
  color: var(--color-text-inverse);
  background-color: var(--color-primary);
  border: none;
}

.btn-primary:hover {
  background-color: var(--color-primary-hover);
}

.btn-secondary {
  color: var(--color-text-inverse);
  background-color: var(--color-secondary);
  border: none;
}

.btn-secondary:hover {
  background-color: var(--color-secondary-dark);
}

.card {
  background-color: var(--color-surface);
  border-radius: 0.75rem;
  box-shadow: var(--shadow-md);
  padding: var(--spacing-lg);
  margin-bottom: var(--spacing-lg);
}

.table-container {
  overflow-x: auto;
  margin: var(--spacing-md) 0;
}

.table {
  width: 100%;
  border-collapse: collapse;
  background-color: var(--color-surface);
}

.table th,
.table td {
  padding: var(--spacing-sm) var(--spacing-md);
  border-bottom: 1px solid var(--color-border-light);
  text-align: left;
}

.table th {
  font-weight: 600;
  color: var(--color-text-secondary);
  background-color: var(--color-surface-hover);
}

.table tr:hover {
  background-color: var(--color-surface-hover);
}

.is-invalid {
  border-color: var(--color-error) !important;
}

.is-valid {
  border-color: var(--color-success) !important;
}

.error-message {
  color: var(--color-error);
  font-size: 0.75rem;
  margin-top: var(--spacing-xs);
}

.success-message {
  color: var(--color-success);
  font-size: 0.75rem;
  margin-top: var(--spacing-xs);
}

@media (max-width: 768px) {
  .grid {
    grid-template-columns: 1fr;
  }

  .table-container {
    margin: var(--spacing-sm) -var(--spacing-md);
  }

  .btn {
    width: 100%;
  }
}

[disabled] {
  opacity: 0.6;
  cursor: not-allowed;
}

:focus-visible {
  outline: 2px solid var(--color-border-focus);
  outline-offset: 2px;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  border: 0;
}

